sp_widget.client_script:
api.controller=function($scope, spUtil) {
  /* widget controller */
  var c = this;
	var data = c.data;

	data.code = "";
	data.alias = "";

	$scope.btnJoinClicked = function() {

		if (!c.data.code) {
			spUtil.addErrorMessage("Code is required!");
			return;
		}
		if (!c.data.alias) {
			spUtil.addErrorMessage("Alias is required!");
			return;
		} 

		c.server.get(c.data).then(function(r) {
			var result = r.data.result;

			if (!result.success) {
				spUtil.addErrorMessage(result.message);
				return;
			}

			alert(JSON.stringify(result, null, 2));

		});
	};
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */


	if (input) {

		var result = {
			success: false,
			message: "Something went wrong",
			game_sys_id: false
		};
		data.result = result;

		if (!input.code) {
			result.message = "Code is required!";
			return;
		}

		if (!input.alias) {
			result.message = "Alias is required!";
			return;
		}

		var gr = new GlideRecord("x_666565_quiznow_game");
		gr.addQuery("code", input.code);
		gr.query();
		if (gr.next()) {
			result.success = true;
			result.message = "Successfully found matching game";
			result.game_sys_id = gr.getUniqueValue();
		} else {
			result.message = "Invalid code";
		}

		data.result = result;
		return;
	}

})();
