sys_script_include.script:
var QuestionChoiceUtils = Class.create();
QuestionChoiceUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


	//return new choices order + 10
    getNewOrder: function(question_id){
		var question = question_id;
		var newOrder = QuestionChoiceUtils.getMaxOrder(question);
		return newOrder + 10;

	},

    type: 'QuestionChoiceUtils',

});

(function(self) {

	//get Max Order from the question choices
    self.getMaxOrder= function(question_id) {
        var question = question_id;

        var gr = new GlideRecord("x_666565_quiznow_question_choice");
        gr.addQuery('question', question);
        gr.orderByDesc('order');
        gr.query();

        if (gr.next() || (!isNaN(gr.order))) {
            return parseInt(gr.order);
        } else {
            return 0;
        }
    };
})(QuestionChoiceUtils);

