sys_script_include.script:
var Game = Class.create();
Game.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    type: 'Game',

	generateCode: function() {
		var code = Game.GenerateCode();
		return JSON.stringify({
			data: code
		}, null, 2);
	}
});


(function(self) {

	self.GenerateCode = function() {
		var charlength = parseInt(gs.getProperty("x_666565_quiznow.game.code.length"));
		if (isNaN(charlength) || !charlength) {
			charlength = 8;
		}
		var randomstring = Math.random().toString(36).slice(-1 * charlength);

		// Check if this already exists
		var gr = new GlideRecord("x_666565_quiznow_game");
		gr.addQuery("code", randomstring);
		gr.query();
		if (gr.getRowCount() > 0) {
			randomstring = self.GenerateCode();
		}

		return randomstring.trim().toUpperCase();
	};

})(Game);
